import React,{useEffect, useState} from 'react';
import { useDispatch ,useSelector} from 'react-redux';
import { getDiets ,filterByDiet,getRecipes} from '../../Redux/actions/actions';

function Diets (){
    
    const dispatch= useDispatch();

    useEffect(()=>{
        dispatch(getDiets());
    },[dispatch])

    useEffect(()=>{
        dispatch(getRecipes());
    },[dispatch])
  

    const diets = useSelector(state => state.diets);

    const handleDiet = (e)=>{
        console.log(e.target.value)
        dispatch(filterByDiet(e.target.value));

    }

    return (
        <div>
             <select>
                {diets.map(d=>
                   
                        <option key={d.id} value={d.name} onClick={(e)=>handleDiet(e)}>{d.name}</option>
                        )}
            </select>
        </div>
    )
}

export default Diets;
