import React from "react";


const Recipe= ({id, name,diets,image})=>{
    
    const getDiets = ()=>{
        let diet= [];
        diets.map(d=> typeof d === "object" ? diet.push(d.name): diets.push(d))

        return diet.join(", ");
    }

    return (
        <div>
            {
            <ul>
                {/* <p>{name}</p> */}
                <p>{getDiets()}</p>
                {/* <p>{diets}</p> */}
                <img src={image} alt="recipe" />
            </ul>

            }
        </div>
    )
}

export default Recipe;
