import React from "react";

let id=1;

const Recipe= ({allRecipes})=>{
    return (
        <div>
            {
                allRecipes.map(r =>(
                    <ul key={r.id}>
                       <img src={r.image} alt="" /> 
                       <br /><br />
                        {r.name}
                        <br /><br />

                       {typeof r.diets ==='object' && !Array.isArray(r.diets) ? r.diets.map(r=> <p key={id++}>{r}</p>) : <p key={id++}>{r.diets}</p>}

                       summary {r.summary}
                       healtscore {r.heatlthScore}
                       steps {r.steps}
                    </ul>
                ))
            }
        </div>
    )
}

export default Recipe;
