import React, {useEffect} from "react";
import { useSelector,useDispatch } from "react-redux";
import {useParams} from "react-router-dom";
import { getRecipeId } from "../../Redux/actions/actions";
import style from "./RecipeId.module.css";

const RecipeId =()=>{
 
const dispatch = useDispatch();
let {id} = useParams();

useEffect(()=>{
    dispatch(getRecipeId(id))
},[id,dispatch])


const recipeId = useSelector(state => state.recipeDetail);


return(
    <div className={style.container}>
        <div className={style.card}>
        {
            recipeId.map(r=> (
                <div key={r.id}>
                    <p>{r.id}</p>
                    <img src={r.image} alt="recipe" />
                    <h4>Receta: {r.name}</h4>
                    <p>Paso a Paso: {typeof id === "number" ? r.steps.map(s=> s.step): r.steps} </p>
                    <p>Resumen del Plato: </p>
                    <p dangerouslySetInnerHTML={{__html: r.summary}}></p>
                    <p>HealthScore: {r.healthScore}</p>
                    <p>Tipo de Plato: {r.dishTypes}</p>
                </div>
            ))
        }
        </div>

    </div>
 )
}


export default RecipeId;
