import React,{useEffect, useState} from 'react';
import { useDispatch ,useSelector} from 'react-redux';
import { getDiets ,filterByDiet,getRecipes} from '../../Redux/actions/actions';

function Diets (){
    
    const dispatch= useDispatch();

    useEffect(()=>{
        dispatch(getDiets());
    },[dispatch])

    useEffect(()=>{
        dispatch(getRecipes());
    },[dispatch])
  

    const [inputDiet, setInputDiet] = useState({
        diet: ""
    });

    const diets = useSelector(state => state.diets);

    const handleDiet = (e)=>{
        setInputDiet(e.target.value);
        // dispatch(filterByDiet(e.target.value));
        console.log(inputDiet);
    }

    return (
        <div>
            <ul>
                {diets.map(d=><li key={d.id} value={inputDiet.diet} onClick={(e)=>handleDiet(e)}>{d.name}</li>)}
            </ul>
        </div>
    )
}

export default Diets;
