import React, {useRef} from "react";
import style from "./Pagination.module.css";

const Pagination =({recipesPerPage,allRecipes,paginated,currentPage})=>{

    const numberPage = [];
    
    for(let i=0; i<Math.ceil(allRecipes / recipesPerPage); i++){
        numberPage.push(i +1);
    }

    const ref = useRef(null);

    console.log('className', ref.current.active)
    return (
        <nav>
            <ul className={style.container}>
                {numberPage && numberPage.map(number =>(
                        <li key={number} onClick={()=>paginated(number)} id="page" className={style.active}>{number}</li>
                ))} 
            </ul>
        </nav>
    )
}

export default Pagination;
