import React  from "react";
import style from "./Pagination.module.css";

const Pagination =({recipesPerPage,allRecipes,paginated,prevPage, nextPage})=>{

    const numberPage = [];
    
    for(let i=0; i<Math.ceil(allRecipes / recipesPerPage); i++){
        numberPage.push(i +1);
    }

    return (
        <nav>
            <div className={style.container}>
            <ul >
                {numberPage && numberPage.map(number =>(
                        <li key={number} onClick={()=>paginated(number)}>{number}</li>
                ))} 
            </ul>
            <button onClick={prevPage} className={style.prev_next}>Prev</button>
            <button onClick={nextPage} className={style.prev_next}>Next</button>
            </div>
        </nav>
    )
}

export default Pagination;
