import React, {useState} from "react";
import {useSelector, useDispatch} from "react-redux";
import Recipe from "./Recipe.jsx";
import style from "./Recipes.module.css";
import Pagination from "../Pagination/Pagination";

import Filter from "../Filters/Filter";
import {sortAlphabetic} from "../../Redux/actions/actions";

const Recipes = ()=>{
    const allRecipes =  useSelector(state=> state.recipes)
    const dispatch = useDispatch();    

    //?paginado
    const [currentPage, setCurrentPage]= useState(1);
    const [recipesPerPage, setRecipesPerPage] = useState(9);
    const lastRecipe = currentPage * recipesPerPage;
    const firstRecipe = lastRecipe - recipesPerPage;
    const recipesPaginated = allRecipes.slice(firstRecipe, lastRecipe);

    const paginated = (numberPage)=>{
        setCurrentPage(numberPage);
    }

    const handleAlphabetical = (e)=>{
        e.preventDefault();
        dispatch(sortAlphabetic(e.target.value));
    }

        return (
            <div>
                <div className={style.container}>
                    <Filter handleAlphabetical={handleAlphabetical}></Filter>

                      {recipesPaginated.map(r=> (
                            <Recipe key={r.id} id={r.id} image={r.image} diets={r.diets} name={r.name}></Recipe>
                        ))
                      
                    }
                </div>
       
               
                
                <Pagination  recipesPerPage={recipesPerPage} allRecipes={allRecipes.length} paginated={paginated} />

            </div>
        )
  

}

export default Recipes;
