import React , {useEffect, useState} from "react";
import NavBar from "../NavBar/NavBar";
import style from "./newRecipe.module.css";
import { getDiets } from "../../Redux/actions/actions";

import { useDispatch,useSelector } from "react-redux";

function NewRecipe(){
    const dispatch = useDispatch();
    
    useEffect(()=>{
        dispatch(getDiets())
    },[dispatch])
    
    const diets = useSelector(state=> state.diets);
    
    return (
        <div>
            <NavBar></NavBar>
            <h1>Crear nueva receta</h1>

            <div className={style.container}>
                <form >
                    <div>
                        <label>Nombre: </label>
                        <input type="text" />
                    </div> 
                    <div>
                        <label>Resumen del plato: </label>
                        <input type="text"></input>
                    </div>
                    <div>
                        <label>Health Score: </label>
                        <input type="number"/>
                    </div>
                    <div className={style.text_select}>
                        <label>Paso a paso: </label>
                        <textarea rows="10" cols="10"/>
                    </div>
                    <div className={style.text_select}>
                        <label>Tipos de dietas: </label>
                        <select>
                            {
                                diets.map(d=> <option key={d.id} value="">{d.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <input type="submit" value="Crear" className={style.create}></input>
                    </div>
                </form>
            </div>
        </div>

    )

}

export default NewRecipe;
