import React from "react";
import style from "./Pagination.module.css";

const Pagination =({recipesPerPage,allRecipes,paginated,currentPage})=>{

    const numberPage = [];
    
    for(let i=0; i<Math.ceil(allRecipes / recipesPerPage); i++){
        numberPage.push(i +1);
    }

   const color = document.getElementById('page');
   color.className  = color.className  === 'active' ? 'inactive': 'active'; 
    return (
        <nav>
            <ul className={style.container}>
                {numberPage && numberPage.map(number =>(
                        <li key={number} onClick={()=>paginated(number)} id="page" className="active">{number}</li>
                ))} 
            </ul>
        </nav>
    )
}

export default Pagination;
