import {GET_RECIPES} from "../actions/actionsType";

const initialState ={
    recipes: []
}

const rootReducer = (state = initialState, action) =>{
    switch(action.type){
        case GET_RECIPES: 
        console.log(action.payload)
            return {
                ...state,
                recipes: action.payload
            }
        default:
            return state;
    }
}

export default rootReducer;
