import {GET_RECIPES,GET_RECIPE_NAME} from "../actions/actionsType";

const initialState ={
    recipes: [],
    allRecipes:[]
}

const rootReducer = (state = initialState, action) =>{
    switch(action.type){
        case GET_RECIPES: 
            return {
                ...state,
                recipes: action.payload,
                allRecipes: action.payload
            }
        case GET_RECIPE_NAME:
            
            let result;
            if(action.payload) {
                result= action.payload
            }else{
                result= "No se encontró la receta";
            } 

            return {
                ...state,
                recipes: action.payload
            }
        default:
            return state;
    }
}

export default rootReducer;
