import React,{useEffect} from 'react';
import { useDispatch ,useSelector} from 'react-redux';
import { getDiets ,filterByDiet,getRecipes} from '../../Redux/actions/actions';

function Diets (){
    
    const dispatch= useDispatch();

    useEffect(()=>{
        dispatch(getDiets());
    },[dispatch])
    useEffect(()=>{
        dispatch(getRecipes());
    },[dispatch])
  

    const diets = useSelector(state => state.diets);

    const handleDiet = (e)=>{
        dispatch(filterByDiet(e.target.value));

    }

    return (
        <div>
            <ul>
                {diets.map(d=><li key={d.id} onClick={(e)=>handleDiet(e)}>{d.name}</li>)}
            </ul>
        </div>
    )
}

export default Diets;
