import React, {useState} from 'react';
import style from "./Filters.module.css";
import {useDispatch} from "react-redux";
import {getRecipeName,sortAlphabetic} from "../../Redux/actions/actions";

function Filter(){
    const dispatch = useDispatch();
    const [input, setInput] = useState('');

    const [order, setOrder] = useState('')
    const [page, setPage] = useState(1);
    const handleChangue =  (e)=>{
        setInput(e.target.value);
        dispatch(getRecipeName(e.target.value));
    }

    const handleAlphabetical = (e)=>{
        e.preventDefault();
        dispatch(sortAlphabetic(e.target.value));
        setPage(1);
        setOrder(`Order ${e.target.value}`);
    }


    return (
        <React.Fragment>
            <div className={style.filters}>
                <div>
                    <input type="text" placeholder="Buscar Receta" value={input} onChange={e=>handleChangue(e)}/>
                </div>
                <div className={style.order}>
                    <button value="ASC" onClick={(e) => handleAlphabetical(e)}>Ordenar A-Z</button>
                    <button value="DESC" onClick={(e) => handleAlphabetical(e)}>Ordenar Z-A</button>
                    <button>Healt Score asc</button>
                    <button>Healt Score desc</button>

                </div>
            </div>
        </React.Fragment>
    )

}

export default Filter;
